import { BrowserRouter as Router, Route, Routes, useNavigate} from "react-router-dom";
import { useState,useEffect } from "react";
import Home from "./components/Home";
import FormElement from "./components/Form";


function App() {

  const [datas, setDatas] = useState([]);

  const onDataRegister =(register) => {
    setDatas([...datas, register]);
  }

  const [reloadTriggered, setReloadTriggered] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const handleBeforeUnload = () => {
      setReloadTriggered(true);
    };

    window.addEventListener('beforeunload', handleBeforeUnload);

    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, []);

  useEffect(() => {
    if (reloadTriggered) {
      navigate('/');
    }
  }, [reloadTriggered, navigate]);
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/form" element={<FormElement onRegister={register => onDataRegister(register)}/>}>
        </Route>
      </Routes>
    </Router>
  );
}

export default App;
