import {Flex, FormControl} from "@chakra-ui/react";
import InputField from "../InputField";
import ButtonAdd from "../Button";
import { useState } from "react";

const Form = (props) => {

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [cpf, serCpf] = useState("");

  const onSave = (e) => {
    e.preventDefault();
    props.onRegister({
      name,
      email,
      cpf,
    });
    console.log(name, email, cpf)
  }

  return(
    <Flex h="100vh" align="center" justify="center" bg="#333">
      <FormControl display="flex" align="center" gap="10px" flexDirection="column" onSubmit={onSave}>
        <InputField
          label="Nome"
          required={true}
          value={name}
          inputChange={value => setName(value)}
        />
        <InputField
          label="E-mail"
          
          value={email}
          inputChange={value => setEmail(value)}
        />
        <InputField
          label="CPF"
          value={cpf}
          inputChange={value => serCpf(value)}
        />
        <ButtonAdd>Adicionar</ButtonAdd>
      </FormControl>
    </Flex>
  );
}

export default Form;
