import { useState } from "react";
import { FormControl, Box, Flex} from "@chakra-ui/react";
import InputField from "../InputField";
import ButtonAdd from "../Button";

const FormElement = (props) => {

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [age, setAge] = useState("");
  const [cpf, serCpf] = useState("");

  const onSave = (e) => {
    e.preventDefault();
    props.onRegister({
      name,
      email,
      cpf,
    });
    console.log(name, email, cpf)
  }

  return(
    <Flex bg="#333" h="100vh" justify="center" align="center">
      <Box as="form" onSubmit={onSave} display="flex" flexDirection="column" justifyContent="center" gap="8px">
        <InputField
            label="Nome"
            required={true}
            type="text"
            value={name}
            inputChange={value => setName(value)}
          />

          <InputField
            label="E-mail"
            required={true}
            type="email"
            value={email}
            inputChange={value => setEmail(value)}
          />

          <InputField
            label="CPF"
            required={true}
            type="number"
            value={cpf}
            inputChange={value => serCpf(value)}
          />
      
          <InputField
            label="Idade"
            required={true}
            type="number"
            value={age}
            inputChange={value => setAge(value)}
          />      
          
      </Box>
    </Flex>
  );
}

export default FormElement;
