import {Input, FormLabel, Flex} from "@chakra-ui/react";

const InputField = (props) => {

  const inputDigit = (e) => {
    props.inputChange(e.target.value)
  }
  return(
    <Flex justify="space-between" align="center" gap="10px">
      <FormLabel color="#fff">
        {props.label}
      </FormLabel>
      <Input bg="#333" color="#fff" border="1px solid #fff" padding="8px" borderRadius="8px" outline="none" fontSize="16px" required={props.required}
        value={props.value} 
        onChange={inputDigit}
      />
    </Flex>
  );
}

export default InputField;
